import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {

  findOdd(arr: number[]) {
    let counter = 0;
    for(let i = 0; i < arr.length; i++) {
      for(let j = 0; j < arr.length; j++) {
        if(arr[i] === arr[j]) {
          counter++;
        }
      }
      if(counter === 2) {
        return arr[i];
      } else {
        counter = 0;
      }
    }
    return 'none';
  }

}
